import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'QLNS';
}
<div class="links">
    <span><a routerLink="/login" routerLinkActive="active">Login</a></span>  &nbsp; 
    <span><a routerLink="/home" routerLinkActive="active">Home</a></span>   &nbsp;
    <span><a routerLink="/add" routerLinkActive="active">ADD</a></span>
    <router-outlet></router-outlet>
</div>
